const { ApiPromise, WsProvider } = require('@polkadot/api');

async function main() {
const wsProvider = new WsProvider('wss://rpc.polkadot.io');
const api = await ApiPromise.create({ provider: wsProvider });
const address = 'FAKEADDRESS';
const balance = await api.query.system.account(address);
console.log(`Balance of ${address}: ${balance}`);
}

const { ApiPromise, WsProvider } = require('@polkadot/api');

async function main() {
// Connect to a local Polkadot node
const wsProvider = new WsProvider('wss://rpc.polkadot.io');
const api = await ApiPromise.create({ provider: wsProvider });

// Retrieve the latest block number
const blockNumber = await api.rpc.chain.getHeader();
console.log(`Latest block number: ${blockNumber.number}`);
}

const Libp2p = require('libp2p');
const TCP = require('libp2p-tcp');
const { NOISE } = require('libp2p-noise');
(async () => {
const libp2p = await Libp2p.create({ modules: { transport: [TCP], connEncryption: [NOISE] }, });
await libp2p.start();
})();

